completion:
table menu {
    name string [unique:true]
    stock int [default:0]
    price int [nullable:false]
}
table transaction {
    timestamp datetime
    food menu ManyToOne OnDelete:SET NULL
    quantity int [nullable:false default:0]
}
table employee {
    user users OneToMany OnDelete:CASCADE OnUpdate:CASCADE nullable:true
    full_name string
    age int
    gender string
    salary int
}
role employee {
    select [users["external_id == user.id"]]
    select insert update delete [menu]
}
role manager {
    select insert update delete [employee menu transaction]
}

table airlines {
    name string
    description string
    rating int [enums:[1 2 3 4 5]]
}
table flights {
    airline airlines ManyToOne
    from string
    to string
    departure_time datetime
    arrival_time datetime
}
table seats {
    flight flights ManyToOne
    seats_code string
    price int
    status string [enums:["booked" "available"]]
    seat_class string [enums:["economy" "premium_economy" "business" "first_class"]]
}
table users {
    name string
    age int
    address string
}

table users{
    full_name string
    birth_date date
    gender string
    address string
    city string
    state string
    zip_code string
}
table home_owners{
    bio string
}
table home_owner_rating {
    score int [enums:[1 2 3 4 5]]
    description string
    home_owner home_owners ManyToOne
}
table rooms_rating {
    score int [enums:[1 2 3 4 5]]
    description string
    room rooms ManyToOne
}
table homes{
    owner users ManyToOne
    address string
    city string
    state string
    zip_code string
    rating int [enums:[1 2 3 4 5]]
}
table rooms{
    home homes ManyToOne
    room_type string
    description string
    price int
    availability boolean
}
table bookings{
    home homes ManyToOne OnDelete:SET NULL
    renter users ManyToOne OnDelete:SET NULL
    start_date date
    end_date date
    status string [enums:["waiting for payment" "paid"]]
}
role home_owner{
    select insert update delete [homes["owner.external_id == user.id"] rooms["owner.external_id == user.id"]]
    select update delete [users["external_id == user.id"]]
    select [bookings["home.owner.external_id == user.id"]]
}
role renter{
    select insert update [bookings["renter.external_id == user.id"]]
    select update delete [users["external_id == user.id"]]
}
